import { readFileSync, writeFileSync, unlinkSync } from 'fs';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

const indexHtmlPath = join(__dirname, '../dist/index.html');

// Read the built index.html
const indexHtml = readFileSync(indexHtmlPath, 'utf-8');

// Write it as a TypeScript file that exports a string
writeFileSync(
  join(__dirname, '../api/index.template.ts'),
  `// Auto-generated by scripts/bundle-html.js - DO NOT EDIT\nexport const HTML_TEMPLATE = ${JSON.stringify(indexHtml)};\n`
);

// Delete dist/index.html so Vercel doesn't serve it as static file
unlinkSync(indexHtmlPath);

console.log('✅ Bundled index.html into api/index.template.ts');
console.log('✅ Deleted dist/index.html to prevent static file serving');
